const path = require('path');
const {
  Builder,
  By,
  Key,
  until
} = require('selenium-webdriver');
let Plan_name, Plan_name2;
let before_creditcard;
describe("fora", () => {
  it("Advisor_Profile - Subscription_TC001", async () => {
    //Check subscription Home page

